    description: 'Alpine Spring Meadows',
  },
  {
    preview:
      'https://cdn.pixabay.com/photo/2019/05/16/21/10/landscape-4208255__340.jpg',
    original:
      'https://cdn.pixabay.com/photo/2019/05/16/21/10/landscape-4208255_1280.jpg',
    description: 'Nature Landscape',
  },
  {
    preview:
      'https://cdn.pixabay.com/photo/2019/05/17/04/35/lighthouse-4208843__340.jpg',
    original:
      'https://cdn.pixabay.com/photo/2019/05/17/04/35/lighthouse-4208843_1280.jpg',
    description: 'Lighthouse Coast Sea',
  },
];

const gallery = document.querySelector('.gallery');
const allImagesEl = [];
let modal;

function createGalleryItem({ preview, description, original }) {
  const liEl = document.createElement('li');
  liEl.className = 'gallery-item';
  const linkEl = document.createElement('a');
  linkEl.className = 'gallery-link';
  linkEl.href = original;
  liEl.appendChild(linkEl);
  const imEl = document.createElement('img');
  imEl.className = 'gallery-image';
  imEl.src = preview;
  imEl.alt = description;
  imEl.dataset.source = original;
  linkEl.appendChild(imEl);
  return liEl;
}

for (const image of images) {
  allImagesEl.push(createGalleryItem(image));
}
gallery.append(...allImagesEl);

const handleEscape = (event) => {
  if (event.key == 'Escape') {
    modal.close();
  }
}

gallery.addEventListener('click', event => {
  if (event.target.nodeName != 'IMG') {
    return;
  }
  event.preventDefault();
  //To be safe
  const { original } = images.find(image => image.description == event.target.alt)
  modal = basicLightbox.create(
    `
    <img src="${original}" width="1112" height="640">
`,
    {
      onShow: () => {
        document.addEventListener('keydown', handleEscape);
      },
      onClose: () => {
        document.removeEventListener('keydown', handleEscape);
      },
    },
  );
  modal.show();
});
